// ThemeSwitch.js

import React, { useState } from 'react';
import { Switch, withStyles } from '@material-ui/core';

const ThemeSwitch = ({ onThemeChange }) => {
  const [isDarkMode, setDarkMode] = useState(false);

  const handleChange = () => {
    setDarkMode(!isDarkMode);
    onThemeChange(!isDarkMode ? 'dark' : 'light');
  };

  const SwitchWithStyles = withStyles({
    switchBase: {
      color: isDarkMode ? '#fff' : '#000',
      '&$checked': {
        color: isDarkMode ? '#fff' : '#000',
      },
      '&$checked + $track': {
        backgroundColor: isDarkMode ? '#333' : '#ddd',
      },
    },
    checked: {},
    track: {},
  })(Switch);

  // return (
  //   <div style={{ display: 'flex', alignItems: 'center' }}>
  //     <span style={{ marginRight: '8px' }}>Light</span>
  //     <SwitchWithStyles checked={isDarkMode} onChange={handleChange} />
  //     <span style={{ marginLeft: '8px' }}>Dark</span>
  //   </div>
  // );
  return (
    <div style={{ display: 'flex', alignItems: 'center', fontFamily: 'Arial, sans-serif' }}>
      <span style={{ marginRight: '12px', fontSize: '16px', color: '#333' }}>Light</span>
      <label style={{ position: 'relative', display: 'inline-block', width: '60px', height: '34px' }}>
        <input
          type="checkbox"
          checked={isDarkMode}
          onChange={handleChange}
          style={{ opacity: 0, width: '0', height: '0' }}
        />
        <span className="slider" style={{ position: 'absolute', cursor: 'pointer', top: '0', left: '0', right: '0', bottom: '0', backgroundColor: '#8ED5FF', borderRadius: '34px', transition: '0.4s' }}></span>
      </label>
      <span style={{ marginLeft: '12px', fontSize: '16px', color: '#333' }}>Dark</span>
    </div>
  );



};

export default ThemeSwitch;
